        $('#termsel').blur();
        var otherSearchParamsURL = "/services/student/bannergetOtherClassSearchParams/termCode=";
        $rootScope.getAPI($rootScope.middlewareServerUrl + otherSearchParamsURL + $scope.selectedvalue, function (data) {
