                          	var endTime = value[0].TIME.split(" ")[1].split("-")[1];
                          	var startHr = startTime.substring(0,2);
                          	var startMin = startTime.substring(2,4);
                          	var endHr = endTime.substring(0,2);
                          	var endMin = endTime.substring(2,4);                          	
                          	var startAMPM = startHr < 12 ? "AM" : "PM";
                          	if(startHr > 12) {
                            	startHr = startHr - 12;
                          	}
                          	var endAMPM = endHr < 12 ? "AM" : "PM";
                          	if(endHr > 12) {
                            	endHr = endHr - 12;
                          	}
                            //value[0].TIME_DISPLAY = value[0].TIME.split(" ")[1];
                          	value[0].TIME_DISPLAY = startHr + ":" + startMin + startAMPM + "-" + endHr + ":" + endMin + endAMPM;
                          	console.log(value[0].TIME_DISPLAY);
                        } else {
                            value[0].TIME_DISPLAY = "To Be Announced";
                        }
                        var days = value[0].TIME.indexOf(" ") != -1 ? value[0].TIME.split(" ")[0] : "";
                        value[0].WEEK_DATA = [{
                            "LABEL": "Mon",
                            "AVAIL": days.indexOf("M") != -1,
                            "DISP_CLASS": days.indexOf("M") != -1 ? "btn-success" : "btn-default"
                        }, {
                            "LABEL": "Tue",
                            "AVAIL": days.indexOf("T") != -1,
                            "DISP_CLASS": days.indexOf("T") != -1 ? "btn-success" : "btn-default"
                        }, {
                            "LABEL": "Wed",
                            "AVAIL": days.indexOf("W") != -1,
                            "DISP_CLASS": days.indexOf("W") != -1 ? "btn-success" : "btn-default"
                        }, {
                            "LABEL": "Thu",
                            "AVAIL": days.indexOf("R") != -1,
                            "DISP_CLASS": days.indexOf("R") != -1 ? "btn-success" : "btn-default"
                        }, {
                            "LABEL": "Fri",
                            "AVAIL": days.indexOf("F") != -1,
                            "DISP_CLASS": days.indexOf("F") != -1 ? "btn-success" : "btn-default"
                        }, {
                            "LABEL": "Sat",
                            "AVAIL": days.indexOf("S") != -1,
                            "DISP_CLASS": days.indexOf("S") != -1 ? "btn-success" : "btn-default"
                        }, {
                            "LABEL": "Sun",
                            "AVAIL": days.indexOf("U") != -1,
                            "DISP_CLASS": days.indexOf("U") != -1 ? "btn-success" : "btn-default"
                        }];
                    });

                }
            });
        });
    } else if (type == "current") {
        $scope.isadd = false;
        $scope.loadCurrentClasses();        
    }

    $scope.registerStudent = function (crn) {
        var endpoint = $rootScope.middlewareServerUrl + "/services/student/bannerregisterStudent";
        var postdata = "termCode=" + $scope.termCode + "&crn=" + crn;
        /*var data = {
            p_msg_out: "Registration request successfully processed."
        };*/
        $rootScope.postAPI(endpoint, postdata, function (data) {
        if (data.p_msg_out) {
            apprise(data.p_msg_out, {
                'verify': false,
                'textYes': "Ok"
            }, function (r) {
                $rootScope.$apply(function () {
                    $location.path("/app/RegistrationB1/RegistrationB1");
                });
            });
        }
        });
    }

    $scope.dropStudent = function (crn) {
        var endpoint = $rootScope.middlewareServerUrl + "/services/student/bannerdropStudent";
        var postdata = "termCode=" + $scope.termCode + "&crn=" + crn;
        /*var data = {
            p_msg_out: "Registration request successfully processed."
        };*/
        $rootScope.postAPI(endpoint, postdata, function (data) {
        if (data.p_msg_out) {
            apprise(data.p_msg_out, {
                'verify': false,
                'textYes': "Ok"
            }, function (r) {
                $scope.loadCurrentClasses();
            });
        }
        });
    }

    $scope.dropStudentConfirm = function (crn) {

        var onConfirm = function (buttonIndex) {
            if (buttonIndex == 1) {
                $scope.dropStudent(crn);
            }
        };
        if (window.device) {
            navigator.notification.confirm(
                'Are you sure you want to drop class?', // message
                onConfirm, // callback to invoke with index of button pressed
                'Drop Class', // title
                ['Yes', 'No'] // buttonLabels
            );
        } else {
            apprise("Are you sure you want to drop class?", {
                'verify': true,
                'textYes': "Yes",
                'textNo': "No"
            }, function (r) {
                if (r) {
                    $scope.dropStudent(crn);
                }
            });
        }

    }
}

