function pageprocessorAddUpdateB1Details(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var curDate = new Date();
    var months = ["JAN", "FEB", "MAR", "APR", "MAY", "JUN", "JUL", "AUG", "SEP", "OCT", "NOV", "DEC"];
    var formattedCurDate = (curDate.getDate() < 10 ? '0' + curDate.getDate() : '' + curDate.getDate()) + "-" + months[curDate.getMonth()] + "-" + curDate.getFullYear();
    $scope.updateContext = $routeParams.flag;

    $scope.dropdownselect = function() {
        var selectedAddrType = $scope.address.ADDRESS_TYPE_CODE;
        if (selectedAddrType == "" || $scope.add == false ) {   
            return;
        }
        var found = false;
        $.each($rootScope.addresses.addresses, function(i, val) {
            if (val.ADDRESS_TYPE_CODE == selectedAddrType) {
                found = true;
            }
        });

        if (found) {
            $scope.address.ADDRESS_TYPE_CODE = "";
            $scope.alert("An address exists for this type. Please select a different address type or go back and edit the existing one.", "Address Exists");
        }
    }
    $scope.deleteAdditional = function(index) {
        $scope.address.additionalphones.pop(index);       

    }
    $scope.addAdditional = function() {
        $scope.address.additionalphones.push({
            "PHONE_SEQ": "",
            "PHONE_CODE": "",
            "COUNTRY_CODE": "",
            "AREA_CODE": "",
            "PHONE_NUMBER": "",
            "EXTENSION": "",
            "INTERNATIONAL_ACCESS": "",
            "UNLISTED_IND": "",
            "visible": true,
            "allowdelete" : true
        });
    }
    var index = $routeParams.index;
    var cindex = $routeParams.cindex;
    if (cindex) {
        $scope.title = "Edit Contact Number";
        $scope.add = false;
        $scope.address = $rootScope.addresses.addresses[$routeParams.index];
        if (cindex != -1) {
            $scope.address.additionalphones[cindex].visible = true;
        } else {
            $scope.addAdditional();
        }

    } else if (index == -1) {
        $scope.title = "Add New Address";
        $scope.add = true;
        $scope.address = {
            "ADDRESS_TYPE_CODE": "",
            "HOUSE_NUMBER": "",
            "STREET_LINE1": "",
            "STREET_LINE2": "",
            "STREET_LINE3": "",
            "STREET_LINE4": "",
            "CITY": "",
            "STATE_CODE": "",
            "ZIP": "",
            "COUNTY_CODE": null,
            "NATION_CODE": "",
            "FROM_DATE": formattedCurDate,
            "TO_DATE": "",
            primaryphone: {
                "COUNTRY_CODE": "",
                "AREA_CODE": "",
                "PHONE_NUMBER": "",
                "EXTENSION": "",
                "INTERNATIONAL_ACCESS": "",
                "UNLISTED_IND": ""
            },
            additionalphones: []
        }

    } else {
        $scope.title = "Edit Address";
        $scope.add = true;
        $scope.editaddress = true;
        $scope.address = $rootScope.addresses.addresses[$routeParams.index];
        $scope.address.FROM_DATE = formattedCurDate;
        $scope.address.TO_DATE = "";
        angular.forEach($scope.address.additionalphones, function(value, key) {
            value.visible = true;
        });
    }
    $scope.alert = function(message, title) {
        if (window.device) {
            navigator.notification.alert(message, null, title, null);
        } else {
            apprise(message, {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {});
        }
    }

    $scope.validateAddress = function() {
        if ($scope.address.ADDRESS_TYPE_CODE == "") {
           $scope.alert("Address Type not selected.","Address");
           return;
        }        
        //Validate city
