            angular.forEach(data.addresses, function(addr, key) {
                var address = addr;
                angular.forEach(data.primaryphones, function(phone, key) {
                    if (phone.ADDRESS_SEQ == address.ADDRESS_SEQ && phone.ADDRESS_TYPE_CODE == address.ADDRESS_TYPE_CODE) {
                        address.primaryphone = phone;
                    }
                });

                address.additionalphones = [];
                angular.forEach(data.additionalphones, function(addphone, key) {
                    if (addphone.ADDRESS_SEQ == address.ADDRESS_SEQ && addphone.ADDRESS_TYPE_CODE == address.ADDRESS_TYPE_CODE) {
                        addphone.toggle = false;
                        addphone.toggleclass = "fa-chevron-right";
                        addphone.delindicator = "";
                        address.additionalphones.push(addphone);
                    }
