function pageprocessorPayStubPage4(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.payment_date = $rootScope.pss.PAY_DATE;
    var url = $rootScope.middlewareServerUrl + "/services/student/bannergetdeductions/yr=" + $rootScope.selyear + "&payno=" + $rootScope.pss.PAYNO + "&seqno=" + $rootScope.pss.SEQNO +
        "&pay_date=" + $rootScope.pss.PAY_DATE_PARAM + "&pict=" + $rootScope.pss.PICT;
    $rootScope.getAPI(url, function(data) {
        $scope.deductions = data;
        $.each($scope.deductions.getytddeductions, function(i, val) {
            var dedrec = $scope.getcurded(val.CODE);
            //$scope.deductions.getytddeductions[i].EMPLOYEE_AMT = dedrec.EMPLOYEE_AMT;
            //$scope.deductions.getytddeductions[i].EMPLOYER_AMT = dedrec.EMPLOYER_AMT;
            //$scope.deductions.getytddeductions[i].APPLICABLE_GROSS_AMT = dedrec.APPLICABLE_GROSS_AMT;
            if (dedrec) {
                val.EMPLOYEE_AMT = dedrec.EMPLOYEE_AMT;
                val.EMPLOYER_AMT = dedrec.EMPLOYER_AMT;
                val.APPLICABLE_GROSS_AMT = dedrec.APPLICABLE_GROSS_AMT;
            } else {
                val.EMPLOYEE_AMT = '';
                val.EMPLOYER_AMT = '';
                val.APPLICABLE_GROSS_AMT = '';
            }

        });
    });
    $scope.getcurded = function(code) {
        var retval = null;

        $.each($scope.deductions.getdeductions, function(j, valded) {
            if (valded.CODE == code) {
                retval = valded;
            }
        });
        return retval;
    }
}

