function pageprocessorFacSchedule(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    if ($rootScope.lastPage == "" || $rootScope.lastPage ==  null) {
        $scope.selterm = "";
        $scope.summary = "";
    } else {
        $rootScope.lastPage = "";
    }


    $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.endpoint, function(data) {
        $scope.termsdropdown = data.facultyterms;
        if ($scope.termsdropdown.length == 1) {
            $scope.selterm = $scope.termsdropdown[0].TERM_CODE;
        }
    });


    $scope.termselect = function(termcode) {
        //$.blockUI();
        $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerfacultydetailschedule/termCode=" + $scope.selterm, function(data) {
            try {
                $('#termlookup').blur();
                $scope.summary = "";
                if (window.device) {
                    var result = JSON.parse(data.p_out[0].summary);
                    data.p_out[0].summary = result;
                } 
                if (data.p_out[0].summary[0].error) {
                    apprise(data.p_out[0].summary[0].error, {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {
                        /*$rootScope.$apply(function() {
                            $location.path("/home");
                        });*/
                    });
                } else {
                    if (data.p_out[0].summary && data.p_out[0].summary.length > 0) {
                        $scope.summary = data.p_out[0].summary;
                        $rootScope.summary = data.p_out[0].summary;
                    } else {
                        apprise("No courses exist for the selected term.", {
                            'verify': false,
                            'textYes': "Ok"
                        }, function(r) {
                            /*$rootScope.$apply(function() {
                                $location.path("/home");
                            });*/
                        });
                    }
                }

            } catch (ex) {
                alert(ex);
            }


        });
    }

    $scope.showDetails = function(index) {
        $rootScope.selterm = $scope.selterm;
        $rootScope.selectedCourse = $scope.summary[index];
        $location.path("/app/FacSchedule/FacSchedulePage2");

    }

}

