                          	var endTime = value[0].TIME.split(" ")[1].split("-")[1];
                          	var startHr = startTime.substring(0,2);
                          	var startMin = startTime.substring(2,4);
                          	var endHr = endTime.substring(0,2);
                          	var endMin = endTime.substring(2,4);                          	
                          	var startAMPM = startHr < 12 ? "AM" : "PM";
                          	if(startHr > 12) {
                            	startHr = startHr - 12;
                          	}
                          	var endAMPM = endHr < 12 ? "AM" : "PM";
                          	if(endHr > 12) {
                            	endHr = endHr - 12;
                          	}
                            //value[0].TIME_DISPLAY = value[0].TIME.split(" ")[1];
                          	value[0].TIME_DISPLAY = startHr + ":" + startMin + startAMPM + "-" + endHr + ":" + endMin + endAMPM;
