            } else {
                addlseqnos += "," + (value.PHONE_SEQ == null ? "" : value.PHONE_SEQ);
                addltelecodes += "," + (value.PHONE_CODE == null ? "" : value.PHONE_CODE);
                addlcountrycodes += "," + (value.COUNTRY_CODE == null ? "" : value.COUNTRY_CODE);
                addlareacodes += "," + (value.AREA_CODE == null ? "" : value.AREA_CODE);
                addlphonenumbers += "," + (value.PHONE_NUMBER == null ? "" : value.PHONE_NUMBER);
