function authFunction(uname, password, $http, $rootScope, success, error) {

    var authSuccess = success;
    var authError = error;

    var sessionExpired = function() {
        $rootScope.ticket = null;
        $rootScope.loggedin = false;
        $rootScope.userroles = null;
        $.jStorage.deleteKey('username');
        $.jStorage.deleteKey('password');
        $.jStorage.deleteKey('ticket');
        $.jStorage.deleteKey('userroles');
        $rootScope.setRoute("/home");
      	var dofocus = function() {
        	$("#loginUsername").val(uname);
          	$("#loginPassword").focus();
      	}
        if (window.device) {
            navigator.notification.alert("Please login again.", dofocus, 'Session Timeout', 'OK');
        } else {
            apprise("Please login again.", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {
                dofocus();
            });
        }
    }

    //Banner Authentication with LDAP - middlware auth

    var url = $rootScope.middlewareServerUrl + "/services/authenticate/login";
    var data = {};
    if (window.device) {
        data = "username=" + uname + "&password=" + password;
    } else {
        url = "/websimulator/json?url=" + url;
        data = {
            method: "POST",
            body: "username=" + uname + "&password=" + password
        };
    }
    var authUrl = url;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
        //Bypassing the ST as there is no service ticket required here..
        callback(tgt);
    };

    $rootScope.postAPI = function(endpoint, postdata, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "POST";
            var proxyData = postdata;
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + serviceurl;
                proxyMethod = "POST";
                proxyData = {
                    method: "POST",
                    body: postdata
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                  	sessionExpired();
                    /*$http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.postAPI(endpoint, postdata, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });*/
                }

            });

        });

    };

    $rootScope.getAPI = function(endpoint, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
              	sessionExpired();
                //alert("Error in service call : " + data + " Status : " + status);
                /*if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }*/

            });

        });

    };




    $.blockUI({
        message: '<div style="position:fixed;left:0px;right:0px;margin: 2px; vertical-align: middle; display: inline-block;text-align: center;color:#fff;"><i class="icon-cog icon-spin icon-4x"></i><h3>Authenticating</h3></div>'
    });

    $http.post(url, data).success(function(data, status, headers, config) {
        success(data);
        $.unblockUI();
    }).error(function(data, status, headers, config) {
        error(data);
        $.unblockUI();
    });
}

