function pageprocessorWAG(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var todayDate = new Date();
    $scope.weekdata = [{
        style: "",
        label: "Mon",
        data: []
    }, {
        style: "",
        label: "Tue",
        data: []
    }, {
        style: "",
        label: "Wed",
        data: []
    }, {
        style: "",
        label: "Thu",
        data: []
    }, {
        style: "",
        label: "Fri",
        data: []
    }, {
        style: "",
        label: "Sat",
        data: []
    }, {
        style: "",
        label: "Sun",
        data: []
    }];

    $scope.selectDay = function (day) {
        for (var i = 0; i < 7; i++) {
            $scope.weekdata[i].style = "";
        }
        $scope.weekdata[day].style = "btn-success";
        $scope.weekataglance = $scope.weekdata[day].data;
    }
    var curDayIndex = todayDate.getDay() == 0 ? 6 : todayDate.getDay() - 1;
    $scope.weekdata[curDayIndex].style = "btn-success";
    $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.dropdownendpoint, function (data) {
        $scope.dropdown = data;
        if ($scope.dropdown.currentTerm.length > 0){
            $scope.selectedvalue = data.currentTerm[0].CURRENT_TERM;
        } else {
            $scope.selectedvalue = data.termsStudentHasClasses[0].TERM_CODE;
        }        
        $scope.dropdownselect();
    });

    $scope.dropdownselect = function (selectedvalue) {
        for (var i = 0; i < 7; i++) {
            $scope.weekdata[i].data = [];
        }
        $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.listendpoint + $scope.selectedvalue, function (data) {
            $scope.listdata = data;
            angular.forEach(data.weekataglance, function (value, key) {
                if (value.MON_DAY != "" && value.MON_DAY != null) {
                    this[0].data.push(value);
                }
                if (value.TUE_DAY != "" && value.TUE_DAY != null) {
                    this[1].data.push(value);
                }
                if (value.WED_DAY != "" && value.WED_DAY != null) {
                    this[2].data.push(value);
                }
                if (value.THU_DAY != "" && value.THU_DAY != null) {
                    this[3].data.push(value);
                }
                if (value.FRI_DAY != "" && value.FRI_DAY != null) {
                    this[4].data.push(value);
                }
                if (value.SAT_DAY != "" && value.SAT_DAY != null) {
                    this[5].data.push(value);
                }
                if (value.SUN_DAY != "" && value.SUN_DAY != null) {
                    this[6].data.push(value);
                }

            }, $scope.weekdata);
            $scope.selectDay(curDayIndex);
        });
    }
}

function pageprocessorLookupClasses(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.selectedSubject = "";
    $scope.courseNumber = "";
    $scope.partTermCode = "";
    $scope.selectedInstructor = "";
    $scope.courseTitle = "";
    $scope.creditFrom = "";
    $scope.creditTo = "";
    $scope.selectedvalue = "";

    var termdropdownURL = "/services/student/bannertermsForClassLookup";
    $rootScope.getAPI($rootScope.middlewareServerUrl + termdropdownURL, function (data) {
        $scope.termdropdown = data.termdropdown;
    });

    $scope.dropdownselect = function (selectedvalue) {
        var otherSearchParamsURL = "/services/student/bannergetOtherClassSearchParams/termCode=";
        $rootScope.getAPI($rootScope.middlewareServerUrl + otherSearchParamsURL + $scope.selectedvalue, function (data) {
            $scope.listdata = data;
        });
    }

    $scope.search = function () {
        if ($scope.selectedvalue == "") {
            if (window.device) {
                alert("Please select term code");
            } else {
                apprise("Please select term code", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {

                });
            }
            return;
        }
        if ($scope.selectedSubject == "" && $scope.selectedInstructor == "") {
            if (window.device) {
                alert("Please select Subject or Instructor");
            } else {
                apprise("Please select Subject or Instructor", {
                    'verify': false,
                    'textYes': "Ok"
                }, function (r) {

                });
            }
            return;
        }
        var searchURL = "/services/student/bannerclasslookup/termCode=" + $scope.selectedvalue +
            "&subjectCode=" + $scope.selectedSubject +
            "&courseNumber=" + $scope.courseNumber +
            "&partTermCode=" + $scope.partTermCode +
            "&facultyPidm=" + $scope.selectedInstructor +
            "&courseTitle=" + $scope.courseTitle +
            "&creditFrom=" + $scope.creditFrom +
            "&creditTo=" + $scope.creditTo;
        $rootScope.getAPI($rootScope.middlewareServerUrl + searchURL, function (data) {
            if (data.CLASSLOOKUP.length == 0) {
                if (window.device) {
                    alert("No record found.");
                } else {
                    apprise("No record found.", {
                        'verify': false,
                        'textYes': "Ok"
                    }, function (r) {

                    });
                }
            } else {
                $rootScope.classlookup = data.CLASSLOOKUP;
                $location.path("/app/LookupClasses/LookupClassesDetails");
            }
        });
    }
}

function pageprocessorLookupClassesDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.classlookup = $rootScope.classlookup;
    angular.forEach($scope.classlookup, function(value, key) {
        if (value[0].TIME.length > 8) {
            var startTime = value[0].TIME.split(" ")[1].split("-")[0];
            var endTime = value[0].TIME.split(" ")[1].split("-")[1];
            var startHr = startTime.substring(0, 2);
            var startMin = startTime.substring(2, 4);
            var endHr = endTime.substring(0, 2);
            var endMin = endTime.substring(2, 4);
            var startAMPM = startHr < 12 ? "AM" : "PM";
            if (startHr > 12) {
